    },
    "5555": { 
      name: "kit", 
      email: "kit@yahoo.com", 
      creditScore: 350
    }
  };

  custID = $custID.toString();
  custname = $custname.toLowerCase();

  const record = customers[custID];

  // ID not found
  if (!record) {
    return { 
      email: null,
      credit_score: null
    };
  }

  // Match name
  if (record.name === custname) {
    return { 
      email: record.email,
      credit_score: record.creditScore
    };
  }

  // Name does not match
  return { 
    email: null,
    credit_score: null
  };

} catch (error) {
  return { 
    email: null,
    credit_score: null,
    error: error.message 
  };
}
