try {

  const customers = {
    "2222": { 
      name: "matt", 
      email: "matt@yahoo.com", 
      prStatus: true
    },
    "4444": { 
      name: "andy", 
      email: "andy@gmail.com", 
      prStatus: false
    }
  };

  custID = $custID.toString();
  custname = $custname.toLowerCase();

  const record = customers[custID];

  // ID not found
  if (!record) {
    return { 
      pr_status: null
    };
  }

  // Match name
  if (record.name === custname) {
    return { 
      pr_status: record.prStatus
    };
  }

  // Name does not match
  return { 
    pr_status: null
  };

} catch (error) {
  return { 
    pr_status: null,
    error: error.message 
  };
}
