    },
    "4444": { 
      name: "andy", 
      nationality: "Non-Singaporean", 
      email: "andy@gmail.com", 
      status: "good-standing"
    },
    "5555": { 
      name: "kit", 
      nationality: "Singaporean", 
      email: "kit@yahoo.com", 
      status: "delinquent"
    }
  };

  custID = $custID.toString();
  custname = $custname.toLowerCase();

  const record = customers[custID];

  // ID not found
  if (!record) {
    return { 
      account_status: null,
      nationality: null
    };
  }

  // Match name
  if (record.name === custname) {
    return { 
      account_status: record.status,
      nationality: record.nationality
    };
  }

  // Name does not match
  return { 
    account_status: null,
    nationality: null
  };

} catch (error) {
  return { 
    account_status: null,
    nationality: null,
    error: error.message 
  };
}
